mapper.select:
select *
from seats where grandPID = #{grandPID}
and parentID = #{parentID}
and number = #{number}
order by number

select *
from seats where grandPID = #{grandPID}
and parentID = #{parentID}
and number = #{number} and state = 1
order by number

mapper.update:
update seats
set state = 3
where seatID = #{seatID}

mapper.select:
select *
from seats where seatID = #{Inf}
and state = 1
